{
  "validationId": "{{faker 'string.uuid'}}",
  "error": {
    "message": "Policy validation failed",
    "code": "policy_violation",
    "description": "Account opening request does not meet policy requirements"
  },
  "failedChecks": [
    "risk_assessment"
  ],
  "recommendations": [
    "Please review your account information",
    "Contact customer support for assistance"
  ],
  "canRetry": true,
  "retryAfter": 300
}

{
  "statusCode": 200,
  "data": {
    "groupCode": "{{body 'groupCode'}}",
    "contracts": [
      {
        "contractId": "{{faker 'string.uuid'}}",
        "contractType": "account-opening",
        "contractName": "Hesap Açılış Sözleşmesi",
        "templateId": "TPL-ACCOUNT-001",
        "contractData": {
          "accountType": "demand-deposit",
          "currency": "TRY"
        }
      },
      {
        "contractId": "{{faker 'string.uuid'}}",
        "contractType": "terms-conditions",
        "contractName": "Genel Hüküm ve Koşullar",
        "templateId": "TPL-TERMS-001",
        "contractData": {
          "version": "2.0"
        }
      },
      {
        "contractId": "{{faker 'string.uuid'}}",
        "contractType": "kvkk",
        "contractName": "KVKK Aydınlatma Metni",
        "templateId": "TPL-KVKK-001",
        "contractData": {
          "consentDate": "{{now 'yyyy-MM-dd'}}"
        }
      }
    ],
    "totalContracts": 3,
    "retrievedAt": "{{now}}"
  }
}

{
  "statusCode": 404,
  "data": {
    "error": {
      "message": "Contract group not found",
      "code": "group_not_found",
      "description": "The specified contract group code does not exist"
    }
  }
}

{
  "statusCode": 500,
  "data": {
    "error": {
      "message": "Document rendering failed",
      "code": "render_failed",
      "description": "Unable to render the contract document"
    }
  }
}

{
  "statusCode": 200,
  "success": true,
  "data": {
    "taskType": "HttpTask",
    "message": "HTTP task test successful",
    "receivedData": {{body}},
    "processedAt": "{{now}}",
    "requestId": "{{faker 'string.uuid'}}"
  },
  "metadata": {
    "endpoint": "api/task-test/http-endpoint",
    "method": "POST",
    "responseTime": {{faker 'number.int' min=50 max=200}}
  }
}

{
  "statusCode": 200,
  "success": true,
  "data": {
    "taskType": "DaprPubsub",
    "message": "HTTP task test successful",
    "receivedData": {{body}},
    "processedAt": "{{now}}",
    "requestId": "{{faker 'string.uuid'}}"
  },
  "metadata": {
    "endpoint": "api/task-test/consume",
    "method": "POST",
    "responseTime": {{faker 'number.int' min=50 max=200}}
  }
}

{
  "statusCode": 200,
  "success": true,
  "data": {
    "taskType": "DaprService",
    "message": "HTTP task test successful",
    "receivedData": {{body}},
    "processedAt": "{{now}}",
    "requestId": "{{faker 'string.uuid'}}"
  },
  "metadata": {
    "endpoint": "api/task-test/dapr-endpoint",
    "method": "POST",
    "responseTime": {{faker 'number.int' min=50 max=200}}
  }
}

{
  "statusCode": 200,
  "success": true,
  "data": {
    "taskType": "NotificationTask",
    "message": "HTTP task test successful",
    "receivedData": {{body}},
    "processedAt": "{{now}}",
    "requestId": "{{faker 'string.uuid'}}"
  },
  "metadata": {
    "endpoint": "api/notification/send",
    "method": "POST",
    "responseTime": {{faker 'number.int' min=50 max=200}}
  }
}

{
  "statusCode": 503,
  "success": false,
  "error": {
    "code": "SERVICE_UNAVAILABLE",
    "message": "Service temporarily unavailable",
    "description": "The service is currently undergoing maintenance or is overloaded",
    "retryAfter": 5
  },
  "timestamp": "{{now}}",
  "requestId": "{{faker 'string.uuid'}}"
}

{
  "statusCode": 500,
  "success": false,
  "error": {
    "code": "INTERNAL_SERVER_ERROR",
    "message": "An unexpected error occurred",
    "description": "Critical system failure - operation cannot be completed",
    "errorType": "HttpRequestException"
  },
  "timestamp": "{{now}}",
  "requestId": "{{faker 'string.uuid'}}"
}

{
  "statusCode": 400,
  "success": false,
  "error": {
    "code": "BAD_REQUEST",
    "message": "Invalid request parameters",
    "description": "The request contains invalid or missing parameters",
    "validationErrors": [
      {
        "field": "testField",
        "message": "Field is required"
      }
    ]
  },
  "timestamp": "{{now}}",
  "requestId": "{{faker 'string.uuid'}}"
}

{
  "statusCode": 200,
  "success": true,
  "data": {
    "message": "This response is intentionally delayed to trigger timeout",
    "delayedBy": "30 seconds"
  },
  "timestamp": "{{now}}",
  "requestId": "{{faker 'string.uuid'}}"
}
